import { useState } from "react";
import TaskDetails from "./TaskDetails.js";
const Tasks = () => {

    const [name,setName] = useState("");
    const [desc, setDesc] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        const task = {name, desc};

        fetch('http://localhost:8000/tasks', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(task)
        }).then(() => {

        })
    }

    return (
        <div className="taskWrap">
            <div className="taskContainer">
                <h1 className="taskHeader">Tasks</h1>
                <TaskDetails />

                <div className="tasks">
                    <input className="taskNameInput" placeholder="What're you up to?"></input>
                    <input className="taskDescInput" placeholder="Add a description!"></input>
                </div>
                <div className="addTaskDiv">
                    <button className="addTaskBtn">Add Task</button>
                </div>
            </div>
        </div>
    );
}

export default Tasks;
