import TaskList from "./TaskList.js";

//This will fetch task data and pass it as prop to Tasklist
const DisplayTask = () => {
    const tasks = JSON.parse(localStorage.getItem('tasks')) || [];
    return (
        <div className="taskDisplayWrap">
            {tasks.length === 0 ? (
                <div className="loading">No tasks found in local storage</div>
            ) : (
                <TaskList tasks={tasks} />
            )}
        </div>
    );
};
export default DisplayTask;
