import { useState, us } from "react";
import useFetch from "./useFetch";

const TaskDetails = () => {
        const { id } = useParams();
        const { data: task, error, isPending } = useFetch(
          `http://localhost:8000/tasks/${task.id}`
        );

    const handleDelete = () => {
        fetch(`http://localhost:8000/tasks/${task.id}`, {
            method: 'DELETE'
        })
        .then(() => {
            console.log(`task ${task.name} deleted`);
        })
    }

    return (
        <div className="taskDetails">
            <h1>{task.name}</h1>
            <p>{task.desc}</p>
            <button onclick={handleDelete}>remove</button>
        </div>
    );
}

export default TaskDetails;
