import { Link } from 'react-router-dom';

// TaskList takes tasks and displays them
const TaskList = ({ tasks, onDelete }) => {


   const handleDelete = (id) => {
     fetch(`http://localhost:8000/tasks/${id}`, {
       method: 'DELETE',
     })
       .then(() => {
         // Manually remove the task from the DOM
         if (taskListRef.current) {
           const taskElement = taskListRef.current.querySelector(
             `[data-task-id="${id}"]`
           );
           if (taskElement) {
             taskElement.remove();
           }
         }
         onDelete(id); // Call onDelete to update parent state
       })
       .catch((error) => console.error('Error deleting task:', error));
   };



  return (
    <div>
      {tasks.map((task) => (
        <div className="taskDisplay" key={task.id}>
          <h1>
            {task.name}
          </h1>
          {task.desc && <p>{task.desc}</p>}
          <button onClick={() => handleDelete(task.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default TaskList;
